import { useState } from "react";


const BasicForm = (props) => {
  const [enterFirstName, setEnterFirstName] = useState('');
  const [enterFirstNameTouch, setEnterFirstNameTouch ] = useState(false);

  const enterNameIsValid = enterFirstName.trim() !== '';
  const enterNameISInvalid = !enterNameIsValid && enterFirstNameTouch;


  const inputChangeHandler = ((event)=>{
    setEnterFirstName(event.target.value);
  })
  const firstNameBlur = (()=>{
    setEnterFirstNameTouch(true);
    if(!enterNameIsValid){
      return;
    }
  })

  const formSubmitHandler = ((event)=>{
    setEnterFirstNameTouch(true);
     event.preventDefault();
     if(!enterNameIsValid){
       return;
     }
     console.log(enterFirstName);
     setEnterFirstName('');
  })

  const FirstnameInputClasses = enterNameISInvalid
    ? 'form-control invalid'
    : 'form-control';


  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className={FirstnameInputClasses}>
          <label htmlFor='name'>First Name</label>
          <input value={enterFirstName} type='text' id='name' onChange={inputChangeHandler} onBlur= {firstNameBlur}/>
          {enterNameISInvalid && <p>please enter a valid name</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
