import { useState } from "react";
const BasicForm = (props) => {
const [firstName, setFirstName]= useState('');
const [firstNameOntouch, setFirstNameOntouch] = useState(false);
const [lastName, setLastName]= useState('');
const [enteredEmail, setEnteredEmail]= useState('');

const firstNameISValid = firstName.trim() !== '';
const lastNameISValid = lastName.trim() !== '';
const emailISValid = enteredEmail.includes('@');

const firstNameVAlidity = !firstNameISValid && setFirstNameOntouch;

const firstNameChangeHandler = ((event)=>{
  setFirstName(event.target.value);
});
const firstNameOnBlur = ((event)=>{
  setFirstNameOntouch(true);
})

const lastNameChangeHandler = ((event)=>{
  setLastName(event.target.value);
});

const emailChangeHandler = ((event)=>{
  setEnteredEmail(event.target.value);
})

const formSubmitHandler = ((event)=>{
   event.preventDefault();
   setFirstNameOntouch(true);
   if(!firstNameISValid){
     return;
   }
   setFirstName('');
})

const firstNameInputClasses = !firstNameVAlidity
? 'form-control invalid'
: 'form-control';


  return (
    <form onSubmit={formSubmitHandler}>
      <div className={firstNameInputClasses}>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={firstNameChangeHandler} onBlur={firstNameOnBlur}/>
          {firstNameVAlidity && <p>first name is not valid</p>}
        </div>

        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' onChange={lastNameChangeHandler} />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='email' id='name' onChange={emailChangeHandler}/>
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
