import { useState } from "react";


const BasicForm = (props) => {
  const [enterFirstName, setEnterFirstName] = useState('');
  const [EnterFirstNameTouched, setEnterFirstNameTouched] = useState(false);

  const enterNameIsValid = enterFirstName.trim() !== '';
  const enterNameIsNotValid = !enterNameIsValid && EnterFirstNameTouched;

  const inputChangeHandler = ((event)=>{
    setEnterFirstName(event.target.value);
  })
  const formSubmitHandler = ((event)=>{
    EnterFirstNameTouched(true)
     event.preventDefault();
     if(!enterNameIsValid){
       return;
     }
     enterNameIsValid;
     console.log(enterFirstName);
     setEnterFirstName('');
  })

  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input value={enterFirstName} type='text' id='name' onChange={inputChangeHandler}/>
          {enterNameIsNotValid && <p>wrong first name</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
