import { useState } from "react";
const BasicForm = (props) => {
const [firstName, setFirstName]= useState('');
const [firstNameisTouched, setFirstNameisTouched] = useState(false);

const firstNameisValid = firstName.trim() !== 0;
const firstNameIsNotValid =  !firstNameisValid && firstNameisTouched;

const firstNameChangeHandler = ((event)=>{
  setFirstName(event.target.value);
})
const firstNameBlur = ((event)=>{
  setFirstNameisTouched(true);
}) 

const formSubmitHandler = ((event)=>{
   event.preventDefault();
   if(!firstName){
     return;
   }
   console.log(firstName);
   setFirstName('');
})

const firstNameInputClasses = firstNameIsNotValid
? 'form-control invalid'
: 'form-control';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={firstNameInputClasses}>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={firstNameChangeHandler} onBlur={firstNameBlur}/>
          {!firstNameIsNotValid && <p>first name is not valid</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
