import useInput from "../hooks/use-input";

const BasicForm = (props) => {
  const{
    value: enterFirstName, 
    isValid: enterFirstNameIsValid,
    hasError: enterFirstNameHasError,
    valueChangeHandler: enterFirstNameChangeHandler,
    inputBlurHandler: enterFirstInputBlurHandler,
    reset: restFirstName,
  } = useInput();
   let formIsValid = false;

const formSubmitHandler = ((event)=>{
   event.preventDefault();
   if(!enterFirstNameIsValid){
     return;
   }
   restFirstName();
})

const firstNameInputClasses = enterFirstNameHasError
? 'form-control invalid'
: 'form-control';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className={enterFirstNameHasError}>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' value={enterFirstName} onChange={enterFirstNameChangeHandler} onBlur={enterFirstInputBlurHandler}/>
          {enterFirstNameHasError&& <p>enter valid first name</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
